%jdbc(hive)
/* Create a column that concatenate transport_id and station_id to have a unique_id for each train at each stop (per day) */
drop table if exists sokim.sbb_data2;

create table sokim.sbb_data2
stored as parquet
as
/* Merge the data from sbb*/
select *, CONCAT(transport_id, ":", station_id) as unique_id
        , CONCAT(transport_type, ":", train_number,  ":", train_type ) as line_id
from sokim.sbb_data

%jdbc(hive)
export table sokim.sbb_Zurich10km to '/user/sokim/data_checkpoint'
/* import external table if not exists sokim.sbb_Zurich10km from '/user/sokim/data_checkpoint'*/

%jdbc(hive)
/* Count the unique ID from the sbb_Zurich10km */
drop table if exists sokim.sbb_countUniqueID;

/* Create an table containing the normal count for each identifier */
create table sokim.sbb_countUniqueID
stored as parquet
as
select unique_id, datetime,
        count(DISTINCT unique_id) as distinct_count, 
        count(unique_id) as normal_count
from sokim.sbb_Zurich10km
group by datetime, unique_id;


select * from sokim.sbb_countUniqueID limit 50;

%jdbc(hive)
/* Subsample the data that have normal_count more than 1 */
drop table if exists sokim.sbb_Zurich10km_nonUniqueData;

create table sokim.sbb_Zurich10km_nonUniqueData
stored as parquet
as
select * FROM sokim.sbb_Zurich10km_withCount 
where normal_count > 1;

select * from sokim.sbb_Zurich10km_nonUniqueData limit 50;

%jdbc(hive)
/* Sample the non-repeated data that has the same unique_id with repeated data*/
drop table if exists sokim.sbb_Zurich10km_uniqueSample_;

create table sokim.sbb_Zurich10km_uniqueSample_
stored as parquet
as
select distinct
    /*a.datetime as datetime,*/
    a.unique_id as unique_id,
    a.normal_count as normal_count,
    if(isnull(from_unixtime(unix_timestamp(a.arriving_time_scheduled, 'dd.MM.yyyy HH:mm'),'HH:mm')),'00:00',
        from_unixtime(unix_timestamp(a.arriving_time_scheduled, 'dd.MM.yyyy HH:mm'),'HH:mm'))  as arriving_time_scheduled,
    /*a.arriving_time_actual as arriving_time_actual,*/
    if(isnull(from_unixtime(unix_timestamp(a.departing_time_scheduled, 'dd.MM.yyyy HH:mm'),'HH:mm')),'00:00',
        from_unixtime(unix_timestamp(a.departing_time_scheduled, 'dd.MM.yyyy HH:mm'),'HH:mm')) as departing_time_scheduled
    /*a.departing_time_actual as departing_time_actual */
    FROM sokim.sbb_Zurich10km_withCount a INNER JOIN sokim.sbb_Zurich10km_nonUniqueData b on(a.unique_id = b.unique_id)
    where a.normal_count = 1;

%jdbc(hive)
/* Choose the latest schudule time among the one with same unique_id.
 By doing this, we can have a sample of one arriving and departing schedule for each unique_id (from non-repeated dataset)*/
drop table if exists sokim.sbb_Zurich10km_uniqueSample;

create table sokim.sbb_Zurich10km_uniqueSample
stored as parquet
as
select unique_id, 
       if(max(arriving_time_scheduled)='00:00', null, max(arriving_time_scheduled)) as arriving_max , 
       if(max(departing_time_scheduled)='00:00', null, max(departing_time_scheduled)) as departing_max
from sokim.sbb_Zurich10km_uniqueSample_
group by unique_id;

select * from sokim.sbb_Zurich10km_uniqueSample limit 50;

%jdbc(hive)
drop table if exists sokim.sbb_Zurich10km_nonUniqueData_Filtered_Final;

create table sokim.sbb_Zurich10km_nonUniqueData_Filtered_Final
stored as parquet
as
select * from sokim.sbb_Zurich10km_nonUniqueData_Filtered
where unique_id !='85:773:24538-01752-1:8594261' or datetime !='21.12.2017';


select * from sokim.sbb_Zurich10km_nonUniqueData_Filtered_Final limit 50;

%jdbc(hive)
/* Merge the unique dataset and the filtered nonunqiue dataset */
drop table if exists sokim.sbb_Zurich10km_filtered;

create table sokim.sbb_Zurich10km_filtered
stored as parquet
as
select * from (select * from sokim.sbb_Zurich10km_nonUniqueData_Filtered_Final 
                union all 
                select * from sokim.sbb_Zurich10km_UniqueData) tmp;

select * from sokim.sbb_Zurich10km_filtered limit 50;

